
function maopao(arr) {
    var arr = arr;
    for (let i = 0; i < arr.length - 1; i++){
        for(let j = 0; j < arr.length - i - 1; j++){
            if(arr[j] > arr[j+1]){
                var num = null;
                num = arr[j+1];
                arr[j+1] = arr[j];
                arr[j] = num;
            }
        }
    }
    return arr;
}

function add(a,b) {
    return a + b;
}
function reduce(a,b) {
    return a - b;
}
function multiply(a,b) {
    return a * b;
}
function except(a,b) {
    return a / b;
}

module.exports = {// 进行对外留接口操作
    //对外接口名 : 真正接口名
    maopao : maopao,
    add : add,
    reduce : reduce,
    multiply : multiply,
    except : except,
}